mapper.select:
select
cid, state, content, type, tid, uid, gid, score
from mini_box.mb_comment


        and cid = #{cid}


        and state = #{state}


        and content = #{content}


        and type = #{type}


        and tid = #{tid}


        and uid = #{uid}


        and gid = #{gid}


        and score = #{score}
