mapper.select:
select
cid, state, content, type, tid, uid, gid, score, create_date, update_date
from minibox.mb_comment


        and cid = #{cid}


        and state = #{state}


        and content = #{content}


        and type = #{type}


        and tid = #{tid}


        and uid = #{uid}


        and gid = #{gid}


        and score = #{score}


        and create_date = #{createDate}


        and update_date = #{updateDate}
