mapper.select:
SELECT id,
uid,
order_id,
order_game_id,
create_date,
update_date,
status,
is_success,
order_cost
FROM mb_order


        id = #{id}


        AND uid = #{uid}


        AND order_id = #{orderId}


        AND order_game_id = #{orderGameId}


        AND `status` = #{status}


        AND is_success = #{success}

mapper.update:
UPDATE mb_order
SET is_success = #{success}
WHERE id = #{id}

mapper.delete:
DELETE
FROM mb_order
WHERE id = #{id}
  AND order_id = #{orderId}
