mapper.select:
select
pid, type, link, uid, tid, cid, gid, state
from mini_box.mb_photo


        and pid = #{pid}


        and type = #{type}


        and link = #{link}


        and uid = #{uid}


        and tid = #{tid}


        and cid = #{cid}


        and gid = #{gid}


        and state = #{state}
