mapper.select:
select
tid, uid, bid, title, content, post_state, create_date, update_date, state
from mini_box.mb_post


        and tid = #{tid}


        and uid = #{uid}


        and bid = #{bid}


        and title = #{title}


        and content = #{content}


        and post_state = #{postState}


        and create_date = #{createDate}


        and update_date = #{updateDate}


        and state = #{state}
