mapper.select:
select
id, order_id, order_game_id, create_date, update_date, status, is_success, order_cost, uid
from minibox.mb_order


        and id = #{id}


        and order_id = #{orderId}


        and order_game_id = #{orderGameId}


        and create_date = #{createDate}


        and update_date = #{updateDate}


        and status = #{status}


        and is_success = #{success}


        and order_cost = #{orderCost}


        and uid = #{uid}

mapper.delete:
DELETE
FROM mb_order
WHERE id = #{id}

    AND order_id = #{orderId}
