mapper.select:
select
id, type, photoLink, uid, tid, cid, gid, state, create_date, update_date
from minibox.mb_photo


        and id = #{id}


        and type = #{type}


        and photoLink = #{photoLink}


        and uid = #{uid}


        and tid = #{tid}


        and cid = #{cid}


        and gid = #{gid}


        and state = #{state}


        and create_date = #{createDate}


        and update_date = #{updateDate}
