mapper.select:
select
id, uid, bid, title, content, post_state, create_date, update_date, state, cover_img
from minibox.mb_post


        and id = #{id}


        and uid = #{uid}


        and bid = #{bid}


        and title = #{title}


        and content = #{content}


        and post_state = #{postState}


        and create_date = #{createDate}


        and update_date = #{updateDate}


        and state = #{state}


        and cover_img = #{coverImg}
