mapper.select:
select
p.id, p.uid, p.bid, p.title, p.content, p.post_state, p.create_date, p.update_date, p.state, p.cover_img
from minibox.mb_post p


        and id = #{id}


        and uid = #{uid}


        and bid = #{bid}


        and title = #{title}


        and content = #{content}


        and post_state = #{postState}


        and create_date = #{createDate}


        and update_date = #{updateDate}


        and state = #{state}


        and cover_img = #{coverImg}


ORDER BY create_date DESC;

SELECT p.id, p.title, p.content, p.post_state, p.create_date, p.state, p.cover_img, u.id author_id, u.nickname author_nickname, u.level author_level, u.user_img author_photo_link, (SELECT COUNT(tid) FROM mb_comment forCount WHERE forCount.tid = p.id) countComment, b.id block_id,b.name block_name FROM mb_post p LEFT JOIN mb_user u ON (u.id = p.uid) LEFT JOIN mb_block b ON(b.id = p.bid)
and p.id = #{id}
and p.uid = #{uid}
and p.bid = #{bid}
and p.title LIKE CONCAT('%',#{title},'%')
and p.content LIKE CONCAT('%',#{content},'%')
and p.post_state = #{postState}
and p.create_date = #{createDate}
and p.update_date = #{updateDate}
and p.state = #{state}
